let updateData = UpdateAPIPageData(
  title = "City Council Approves Budget",
  url = "news/city-council-budget-2025",
  description = "Highlights of the approved 2025 municipal budget",
  allowComments = true,
  includeInSitemap = true,
  tags = @["local", "budget", "2025"]
)

let (response, httpResponse) = client.patchPage(tenantId = "my-tenant-123", id = "page-456", updateAPIPageData = updateData)

if response.isSome:
  let patched = response.get()
  echo "Patched page title: " & patched.title
else:
  echo "Patch failed, HTTP status: " & $httpResponse.status
